begin
wwv_flow_imp_shared.create_flow_process(
 p_id=>wwv_flow_imp.id(4916061052839801951)
,p_process_sequence=>1
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'AJAX_GET_PRODUCT_IMAGE'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_lob          BLOB;',
'  l_length       NUMBER;',
'  l_mimetype     VARCHAR2(2000);',
'  l_filename     VARCHAR2(2000);',
'  l_product_id      number;',
'begin',
'  l_product_id := v(''G_PRODUCT_IMAGE_ID'');',
'',
'  begin',
'    select product_image',
'         , null mimetype',
'         , null filename',
'      into l_lob',
'         , l_mimetype',
'         , l_filename',
'      from demo_product_info',
'     where nvl(dbms_lob.getlength(product_image),0) > 0',
'       and product_id = l_product_id;',
'',
'  exception',
'    when no_data_found then',
'    null;',
'/*       select file_content',
'           ,   mime_type',
'           ,   file_name',
'        into l_lob',
'           , l_mimetype',
'          , l_filename',
'       from APEX_WORKSPACE_STATIC_FILES',
'      where FILE_NAME = ''xxxxxx''; */',
'  end;',
'',
'  --',
'  l_length := DBMS_LOB.getlength(l_lob);',
'  --',
'  htp.flush;',
'  htp.init;',
'  --',
'  owa_util.mime_header(nvl(l_mimetype,''application/octet''), FALSE);',
'',
'  htp.p(''Content-length:'' || l_length);',
'  htp.p(''Content-Disposition:inline;filename="'' || l_filename || ''"'');',
'  --',
'  -- close the headers',
'  owa_util.http_header_close;',
'  --',
'  -- download the BLOB',
'  wpg_docload.download_file(l_lob);',
'  apex_application.stop_apex_engine;',
'--',
' ',
'  exception',
'    WHEN apex_application.e_stop_apex_engine THEN',
'    NULL;',
'    when OTHERS then',
'      null;',
' end;'))
,p_process_clob_language=>'PLSQL'
,p_security_scheme=>'MUST_NOT_BE_PUBLIC_USER'
,p_version_scn=>15601462143025
);
