'        }',
'        if (button._hidePanelHandler) {',
'            button.removeEventListener(''mouseleave'', button._hidePanelHandler);',
'        }',
'    });',
'    ',
'    // Limpiar event listeners de la ventana',
'    if (window._resizeHandler) {',
