'      p_directory => p_plugin.file_prefix,',
'      p_version   => null',
'    );',
'  else',
'    apex_css.add_file(',
'      p_name      => ''alertify.bootstrap'',',
'      p_directory => p_plugin.file_prefix,',
'      p_version   => null',
'    );',
'  end if;',
'',
'  l_render_result.attribute_03 := l_notification_type;',
'  l_render_result.attribute_04 := l_message;',
