,p_javascript_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'// ===========================================================================',
'// Init Carousel',
'',
unistr('let slideIndex = {}; // Objeto para almacenar \00EDndices de slides por ID \00FAnico'),
'',
'function initializeSlides() {',
'  // Encuentra todos los elementos con clase ''slideshow-container''',
'  const allContainers = document.querySelectorAll(''.slideshow-container'');',
'',
'  allContainers.forEach((container) => {',
'    const id = container.getAttribute(''data-id''); // Obtiene el ''data-id'' del contenedor',
'',
unistr('    // Si el ID no est\00E1 ya en slideIndex, lo agregamos'),
'    if (!slideIndex[id]) {',
unistr('      slideIndex[id] = 1; // Inicializa el \00EDndice del nuevo carrusel'),
'      showSlides(1, id);  // Muestra el primer slide del nuevo carrusel',
'    }',
'',
unistr('    // Si el carrusel ya tiene m\00E1s de un slide, aseguramos que el \00EDndice est\00E9 actualizado'),
'    const slides = container.querySelectorAll(''.slide'');',
'    if (slides.length > 1 && slideIndex[id] > slides.length) {',
unistr('      slideIndex[id] = slides.length;  // Aseguramos que el \00EDndice no exceda el n\00FAmero de slides'),
'    }',
'  });',
'}',
'',
'function plusSlides(n, id) {',
unistr('  if (!slideIndex[id]) return; // Aseg\00FArate de que el ID exista en slideIndex'),
'  showSlides((slideIndex[id] += n), id);',
'}',
'',
'function showSlides(n, id) {',
'  let i;',
'  const slides = document.querySelectorAll(`.slideshow-container[data-id="${id}"] .slide`);',
'  if (!slides.length) return; // Si no hay slides, no hace nada',
'',
unistr('  // L\00F3gica para circular a trav\00E9s de los slides'),
'  if (n > slides.length) { slideIndex[id] = 1; }',
'  if (n < 1) { slideIndex[id] = slides.length; }',
'',
'  for (i = 0; i < slides.length; i++) {',
'    slides[i].style.display = "none";',
'  }',
