'  begin',
'    l_ok_label := nvl(l_ok_btn_label, nvl(l_def_ok_btn_label, ''OK''));',
'    l_cancel_label := nvl(l_cancel_btn_label, nvl(l_def_cancel_btn_label, ''Cancel''));',
