unistr('        raise_application_error(-20002, ''Error: El base64 est\00E1 vac\00EDo.'');'),
'    elsif l_length > 32767 then',
'        raise_application_error(-20003, ''Error: El base64 es demasiado grande para procesar.'');',
'    end if;',
