'    for i in 1 .. apex_application.g_f01.count loop',
'        l_full_base64 := l_full_base64 || apex_application.g_f01(i);',
'    end loop;',
'',
'    -- Remover el prefijo ''data:image/png;base64,'' si existe',
'    if instr(l_full_base64, '','') > 0 then',
