'',
'',
'  apex_json.open_object;',
'  apex_json.write(''success'',true);',
'  apex_json.write(''l_length'',l_length);',
'  apex_json.close_object;',
'',
'exception',
'  when others then',
'    apex_json.open_object;',
'    apex_json.write(''success'',false);',
'    apex_json.write(''error_msg'',sqlerrm);',
