require(['ojs/ojcore', 'knockout', 'jquery', 'ojs/ojarraydataprovider', 'ojs/ojknockout', 'ojs/ojtable'],  
function(oj, ko, $, ArrayDataProvider)  
{     
  function tableViewModel(dataArray)  
  {  
    var self = this;  
    self.dataprovider = new ArrayDataProvider(dataArray, {keyAttributes: 'Year'});  

    self.handleDropRows = function(event, ui) {  
      var dragData = event.dataTransfer.getData('application/ojtablerows+json');  

      if (dragData)  
      {  
        var rowDataArray = JSON.parse(dragData);  
        var toRowIndex = ui['rowIndex'];  
        var fromRowIndex = rowDataArray[0]['index'];  
        dataArray.splice(fromRowIndex, 1);  
        if (fromRowIndex < toRowIndex)  
        {  
          dataArray.splice(toRowIndex - 1, 0, rowDataArray[0]['data']);  
        }  
        else  
        {  
          dataArray.splice(toRowIndex, 0, rowDataArray[0]['data']);    
        }  
      }  
    };  
  }  

  $(document).ready  
  (  
    function()  
    {  
      dataArray = ko.observableArray([]);  
      ko.applyBindings(new tableViewModel(dataArray), document.getElementById('table'));  
    }  
  );  
});

